sp_widget.client_script:
api.controller = function ($scope, $rootScope, spModal, spUtil) {
	/* widget controller */

	var c = this;
	$rootScope.$on('selectNote', function (event, data) {
		console.log('Listener caught NoteID: ' + $rootScope.noteID);
		c.server.get({
			action: 'getNote',
			noteID: $rootScope.noteID
		}).then(function (r) {
			c.data.title = r.data.note.title;
			c.data.note = r.data.note.note;
			c.data.noteID = r.data.note.sys_id;

		});
	});

	c.updateNote = function (updateType) {
		console.log('prueba');
		c.server.get({
			action: 'updateNote',
			noteID: c.data.noteID,
			noteBody: c.data.note,
			noteTitle: c.data.title
		}).then(function (r) {
			console.log("resultado:" + r.data.note.title);
			$rootScope.$emit('actualizarNote', r.data.note);
		});
	};

	c.confirmDelete = function () {
		spModal.confirm("Are you sure you want to delete this Note record?").then(function (confirmed) {
			if (confirmed) {
				c.deleteNote();

			}
		});
	};

	c.deleteNote = function () {
		c.server.get({
			action: 'deleteNote',
			noteID: c.data.noteID
		}).then(function (r) {
			spUtil.addTrivialMessage( "The" + c.data.title + "Record has been deleted");
			$rootScope.$emit('actualizarNote', r.data.note);
			c.data.title='';
			c.data.note='';
			c.data.noteID='';
		});
	};


	$rootScope.$on('snDeleteNote', function (event, data) {
		debugger;
		c.server.get({
			action: 'deleteNote',
			noteID: data

		  }).then(function(r){
			  debugger;
			//c.data.notes = r.data.notes;
		  });
	});

};

sp_widget.script:
(function() {
  /* populate the 'data' object */
  /* e.g., data.table = $sp.getValue('table'); */
  if (input && input.noteID) {
    gs.info('input.noteID' +input.noteID);
    var note = new GlideRecord('x_647573_innovate_note');
    if (note.get(input.noteID)) {
      if (input.action == 'getNote') {
        data.note = {};
        $sp.getRecordValues(data.note, note, "title, note, sys_id");
      }
      else if (input.action == 'updateNote') { 
        gs.info('input.noteTitle' +input.noteTitle);
        gs.info('input.noteBody' +input.noteBody);
        note.title = input.noteTitle; 
        note.note = input.noteBody; 
        note.update(); 
        data.note = {};
        $sp.getRecordValues(data.note, note, "title, note, sys_id");
      }
      else if (input.action == 'deleteNote') { 
      gs.info('Registro eliminado');
        note.deleteRecord();
        // data.note = {};
        // $sp.getRecordValues(data.note, note, "title, note, sys_id");
      }
    }
  }
})();
