sys_variable_value.value:
(function execute(inputs, outputs) {
  // ... code ...
  var list=[];
  var count=0;
  var task = new GlideRecord(Constants.TASK_TABLE);
  if (inputs.filter) {
    task.addEncodedQuery(inputs.filter);
  }
  task.query();

  while (task.next()) {
    ++count;
    var obj = {
      "assigned_to": task.getValue('assigned_to'),
      "short_description": task.getValue('short_description'),
      "state": parseInt(task.getValue('state'))
    };
    list.push(obj);
   // gs.info(JSON.stringify(list,null,4));
  }
  outputs.list=list;
  outputs.count=count;
})(inputs, outputs);

