sys_script_include.script:
var IncidentUtils = Class.create();
IncidentUtils.prototype = {
    initialize: function () {
    },
    getLatesIncident: function (num) {
        var limit = num || 5;
        var result = [];
        var record = new GlideRecord('x_647573_innovate_needit');
        record.orderByDesc('sys_created_on');
        record.setLimit(limit);
        record.query();
        while (record.next()) {
            result.push(record.number.getDisplayValue());
        }
        return result;
    },
    grabRecords:function(tab,lim,orden){
        var result = [];
       var tabla=tab||'x_647573_innovate_needit';
       var limit=lim||5;
       var orderby=orden||'sys_created_on'
        var record = new GlideRecord(tabla);
        record.orderByDesc(orderby);
        record.setLimit(limit);
        record.query();
        while (record.hasNext()) {
            result.push(record.number.getDisplayValue());

        }
        return result;
    },

    type: 'IncidentUtils'
};
