sys_script_include.script:
var ServiceNow201GlideAjax = Class.create();
ServiceNow201GlideAjax.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
	getIncidentStatus: function () {
		var incidentNumber = this.getParameter('sysparm_incident_number');
		if (!gs.nil(incidentNumber)) {
			var record = new GlideRecord('x_647573_innovate_needit');
			record.get('number', incidentNumber);
			return record.state.getDisplayValue();
		} else {
			return "No se encontro incidentes";
		}
	},
	getLatesIncident: function () {
		gs.info("Consultando getlaetsincident");

		var incidents = [];
		var responseObject = {};
		var limit = parseInt(this.getParameter('sysparm_limit'));
		var shortDescription = this.getParameter('sysparm_shortDesc');
		//if (!gs.nil(limit) && (typeof limit == 'number') && !gs.nil(shortDescription)) {
			if (!gs.nil(limit) && (typeof limit == 'number')) {
			var record = new GlideRecord('x_647573_innovate_needit');
			//record.addQuery('short_description','CONTAINS', shortDescription);
			record.orderByDesc('sys_created_on');
			record.setLimit(limit);
			record.query();
			while (record.next()) {
				responseObject = {};
				responseObject.number = record.number.getDisplayValue();
				responseObject.sysID = record.sys_id.getDisplayValue();
				responseObject.shortDescription = record.short_description.getDisplayValue();
			gs.info("Informacion3----------:" +record.short_description.getDisplayValue() +' ' +record.number.getDisplayValue() + ' =>' + record.sys_id.getDisplayValue());
				incidents.push(responseObject);
			}
		//	gs.info("Informacion json2----------:" +global.JSON().encode(incidents));

			return new global.JSON().encode(incidents);
		} else {
			return 'No encontro informacion'

		}
	},
	sayHello: function () {
		return "Hola mundo!!";
	},
	type: 'ServiceNow201GlideAjax'
});
