sys_ws_operation.operation_script:
(function process(/*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {

    // implement resource herea
  var gr;
    var inc = [];
    var requestBody = request.body;
    var requestData = requestBody.data;
gs.info("Inicio");
    for (i = 0; i < requestData.length; i++) {
		gs.info("entro");
        gr = new GlideRecord('x_647573_innovate_needit');
        gr.initialize();
		gs.info("requestData[i].u_requested_for" +  requestData[i].u_requested_for);
		gs.info("requestData[i].short_description" + requestData[i].short_description);
		gs.info(" requestData[i].u_when_needed" +   requestData[i].u_when_needed);

       // gr.setValue('u_requested_for', requestData[i].u_requested_for);
	   gr.u_requested_for.setDisplayValue(requestData[i].u_requested_for);
		//gr.u_requested_for="5137153cc611227c000bbd1bd8cd2005";
		gr.short_description=requestData[i].short_description;
		gr.u_when_needed=requestData[i].u_when_needed;
		gr.u_requested_for_email=requestData[i].u_requested_for_email;

		//gr.setValue('u_requested_for').u_requested_for.setDisplayValue(requestData[i].u_requested_for);
        //gr.setValue('short_description', requestData[i].short_description);
        //gr.setValue('u_when_needed', requestData[i].u_when_needed);
		  //gr.setValue('u_requested_for_email', requestData[i].u_requested_for_email);
        gr.insert();
        gr.addQuery("number", gr.number);
        gr.query()


        if (gr.next()) {
			gs.info("result");
            inc.push({
                'Number': gr.number,
                'Request type': gr.u_request_type.getDisplayValue(),
                'Descripcion': gr.short_description
            })

        }
    }
    response.setBody(inc);
})(request, response);
