sysauto_script.script:
// Get today's time and date
var fechaActual = new GlideDateTime();
var fechaFutura = new GlideDateTime();
fechaFutura.addDaysLocalTime(2);
gs.info("ejecucion schedules  fechaActual:" + fechaActual);
gs.info("ejecucion schedules  dentro de 24 horas xx:" + gs.hoursAgo(-48));
// Query the database for NeedIt Task records with Due date field values older
// than the current time.  Only return NeedIt Task records that do not have
// a State field value of Closed Complete.
var overdueNITasks = new GlideRecord('x_647573_innovate_needit');
overdueNITasks.addQuery('due_date', '>=', fechaActual);
overdueNITasks.addQuery('due_date', '<', gs.hoursAgo(-48));
overdueNITasks.addQuery('state', '!=', 3);

overdueNITasks.query();
//gs.info("overdueNITasks"+overdueNITasks);
//gs.debug("This is a debug message from {0}", overdueNITasks);
gs.info(gs.nil(overdueNITasks));
// Write a log message for each overdue NeedIt Task Record
while (overdueNITasks.next()) {
    gs.info("Overdue NeedIt Task record24horas = " + overdueNITasks.number);
	//gs.info(" overdueNITasks.u_employee.name:"+ JSON.stringify(overdueNITasks));
    gs.eventQueue('x_647573_innovate.dueSoonNeedItTasks', overdueNITasks, overdueNITasks.number,  gs.getUserName(),'my_queue');

}
